// Type definitions for ErrorStackParser v2.0.0
// Project: https://github.com/stacktracejs/error-stack-parser
// Definitions by: Eric Wendelin <https://www.eriwen.com>
// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped

declare module ErrorStackParser {
    export interface StackFrame {
        isConstructor?: boolean;
        isEval?: boolean;
        isNative?: boolean;
        isTopLevel?: boolean;
        columnNumber?: number;
        lineNumber?: number;
        fileName?: string;
        functionName?: string;
        source?: string;
        args?: any[];
        evalOrigin?: StackFrame;

        constructor(object: StackFrame): StackFrame;

        getIsConstructor(): boolean;

        setIsConstructor(): void;

        getIsEval(): boolean;

        setIsEval(): void;

        getIsNative(): boolean;

        setIsNative(): void;

        getIsTopLevel(): boolean;

        setIsTopLevel(): void;

        getColumnNumber(): number;

        setColumnNumber(): void;

        getLineNumber(): number;

        setLineNumber(): void;

        getFileName(): string;

        setFileName(): void;

        getFunctionName(): string;

        setFunctionName(): void;

        getSource(): string;

        setSource(): void;

        getArgs(): any[];

        setArgs(): void;

        getEvalOrigin(): StackFrame;

        setEvalOrigin(): void;

        toString(): string;
    }

    /**
     * Given an Error object, extract the most information from it.
     *
     * @param {Error} error object
     * @return {Array} of StackFrames
     */
    export function parse(error: Error): StackFrame[];
}

export = ErrorStackParser;
